mapper.update:
UPDATE alphafrog_agent_run
SET status = #{status},
    updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}
  AND user_id = #{userId}

UPDATE alphafrog_agent_run
SET status = #{status},
    plan_json = CAST(#{planJson} AS jsonb),
    updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}
  AND user_id = #{userId}

UPDATE alphafrog_agent_run SET status = #{status}, snapshot_json = CAST(#{snapshotJson} AS jsonb), last_error = #{lastError}, updated_at = CURRENT_TIMESTAMP
, completed_at = CURRENT_TIMESTAMP
WHERE id = #{id} AND user_id = #{userId}
