mapper.select:
SELECT id, user_id, name, visibility, tags::text AS tags_json, status, timezone, ext::text AS ext_json, created_at, updated_at FROM alphafrog_portfolio WHERE user_id = #{userId}
AND status = #{status}
AND name ILIKE CONCAT('%', #{keyword}, '%')
ORDER BY created_at DESC OFFSET #{offset} LIMIT #{limit}

SELECT COUNT(1)
FROM alphafrog_portfolio
WHERE user_id = #{userId}

    AND status = #{status}


    AND name ILIKE CONCAT('%', #{keyword}, '%')

SELECT COUNT(1)
FROM alphafrog_portfolio
WHERE user_id = #{userId}
  AND name = #{name}
  AND status = 'active'

mapper.update:
UPDATE alphafrog_portfolio
SET status = 'archived',
    updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}
  AND user_id = #{userId}
