mapper.select:
SELECT id, strategy_id, user_id, run_time, start_date, end_date, params_json::text AS params_json, status, created_at, updated_at, ext::text AS ext_json FROM alphafrog_strategy_backtest_run WHERE strategy_id = #{strategyId} AND user_id = #{userId}
AND status = #{status}
ORDER BY run_time DESC, id DESC OFFSET #{offset} LIMIT #{limit}

SELECT COUNT(1)
FROM alphafrog_strategy_backtest_run
WHERE strategy_id = #{strategyId}
  AND user_id = #{userId}

    AND status = #{status}
