mapper.select:
SELECT id, portfolio_id, user_id, name, description, rule_json::text AS rule_json, rebalance_rule, capital_base, start_date, end_date, status, created_at, updated_at, ext::text AS ext_json FROM alphafrog_strategy_definition WHERE user_id = #{userId}
AND status = #{status}
AND name ILIKE CONCAT('%', #{keyword}, '%')
ORDER BY created_at DESC OFFSET #{offset} LIMIT #{limit}

SELECT COUNT(1)
FROM alphafrog_strategy_definition
WHERE user_id = #{userId}

    AND status = #{status}


    AND name ILIKE CONCAT('%', #{keyword}, '%')

mapper.update:
UPDATE alphafrog_strategy_definition
SET status = 'archived',
    updated_at = CURRENT_TIMESTAMP
WHERE id = #{id}
  AND user_id = #{userId}
