mapper.select:
SELECT id,
       run_id,
       user_id,
       trade_date,
       nav,
       return_pct,
       benchmark_nav,
       drawdown,
       created_at
FROM alphafrog_strategy_nav
WHERE run_id = #{runId}
  AND user_id = #{userId}

    AND trade_date >= #{from}


    AND trade_date <= #{to}

ORDER BY trade_date ASC, id ASC
OFFSET #{offset}
LIMIT #{limit}

SELECT COUNT(1)
FROM alphafrog_strategy_nav
WHERE run_id = #{runId}
  AND user_id = #{userId}

    AND trade_date >= #{from}


    AND trade_date <= #{to}
