mapper.select:
SELECT id,
       portfolio_id,
       user_id,
       symbol,
       event_type,
       quantity,
       price,
       fee,
       slippage,
       trade_time,
       settle_date,
       note,
       payload::text AS payload_json,
       created_at
FROM alphafrog_portfolio_trade
WHERE portfolio_id = #{portfolioId}
  AND user_id = #{userId}

    AND trade_time >= #{from}


    AND trade_time <= #{to}


    AND event_type = #{eventType}

ORDER BY trade_time DESC, id DESC
OFFSET #{offset}
LIMIT #{limit}

SELECT COUNT(1)
FROM alphafrog_portfolio_trade
WHERE portfolio_id = #{portfolioId}
  AND user_id = #{userId}

    AND trade_time >= #{from}


    AND trade_time <= #{to}


    AND event_type = #{eventType}
